// src/QuestionsList.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function QuestionsList({ token }) {
  const [questions, setQuestions] = useState([]);

  // Fetch all questions when the component mounts
  useEffect(() => {
    axios.get('http://localhost:5001/api/questions')
      .then((res) => {
        console.log("Fetched questions:", res.data);
        setQuestions(res.data);
      })
      .catch((err) => console.error("Error fetching questions:", err));
  }, []);

  // Handle upvoting a question
  const handleUpvote = (id) => {
    axios.post(
      `http://localhost:5001/api/questions/${id}/upvote`,
      {},
      { headers: { Authorization: `Bearer ${token}` } }
    )
      .then((res) => {
        console.log("Upvote response:", res.data);
        // Update the question's upvote count in the state
        setQuestions((prevQuestions) =>
          prevQuestions.map((q) =>
            q.id === id ? res.data.question : q
          )
        );
      })
      .catch((err) => console.error("Error upvoting question:", err));
  };

  return (
    <div className="container" style={{ marginTop: '20px' }}>
      <h2>Questions List</h2>
      {questions.length === 0 ? (
        <p>No questions found.</p>
      ) : (
        questions.map((q) => (
          <div key={q.id} style={{ padding: '15px', marginBottom: '10px', border: '1px solid #ccc', borderRadius: '5px', background: '#fff' }}>
            <h3>{q.title}</h3>
            <p>{q.body}</p>
            <p>Upvotes: {q.upvotes}</p>
            <button onClick={() => handleUpvote(q.id)} style={{ padding: '5px 10px', cursor: 'pointer' }}>
              Upvote
            </button>
          </div>
        ))
      )}
      {/* Navigation link to the Chat page */}
      <div style={{ textAlign: 'center', marginTop: '20px' }}>
        <Link to="/chat" style={{ fontSize: '18px', color: '#5151e5', textDecoration: 'none', fontWeight: 'bold' }}>
          Go to Chat
        </Link>
      </div>
    </div>
  );
}

export default QuestionsList;
