// src/GroupChat.js
import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function GroupChat({ user, token, socket }) {
  const { groupId } = useParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  useEffect(() => {
    // Join the Socket.io room for this group
    socket.emit("joinGroup", groupId);
    console.log(`Joined group-${groupId}`);

    // Fetch existing messages
    axios.get(`http://localhost:5001/api/group-chat/${groupId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => {
      console.log("Fetched group messages:", res.data);
      setMessages(res.data);
    })
    .catch((err) => console.error("Error fetching group messages:", err));

    // Listen for new group messages
    socket.on("receiveGroupMessage", (messageData) => {
      console.log("New group message:", messageData);
      // Only add if it belongs to this group
      if (parseInt(messageData.group_id) === parseInt(groupId)) {
        setMessages((prev) => [...prev, messageData]);
      }
    });

    return () => {
      // Cleanup the event listener
      socket.off("receiveGroupMessage");
    };
  }, [groupId, socket, token]);

  const handleSendMessage = () => {
    if (!newMessage.trim()) return;
    const payload = {
      groupId: parseInt(groupId),
      userId: user.id,
      message: newMessage.trim()
    };
    // Emit the group message event
    socket.emit("sendGroupMessage", payload);

    // Optimistic UI update
    setMessages((prev) => [
      ...prev,
      {
        group_id: groupId,
        user_id: user.id,
        message: newMessage.trim(),
        created_at: new Date().toISOString(),
        username: user.username
      }
    ]);
    setNewMessage('');
  };

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <h2>Group Chat - Group {groupId}</h2>
      <div style={{
        border: '1px solid #ccc',
        height: '300px',
        overflowY: 'scroll',
        marginBottom: '10px',
        padding: '10px'
      }}>
        {messages.map((msg, idx) => (
          <div
            key={idx}
            style={{
              margin: '10px 0',
              padding: '8px',
              background: msg.user_id === user.id ? '#e0ffe0' : '#f0f0f0',
              borderRadius: '5px'
            }}
          >
            <strong>{msg.username}</strong>: {msg.message}
            <div style={{ fontSize: '0.8em', color: '#555' }}>
              {new Date(msg.created_at).toLocaleTimeString()}
            </div>
          </div>
        ))}
      </div>
      <div>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          style={{ width: '80%', padding: '8px' }}
          placeholder="Type a message"
        />
        <button onClick={handleSendMessage} style={{ padding: '8px 16px', marginLeft: '10px' }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default GroupChat;
