// src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import Login from './Login';
import Chat from './Chat';

// Establish connection to the WebSocket server
const socket = io('http://localhost:5001');

function App() {
  const [user, setUser] = useState(null); // will hold user details
  const [token, setToken] = useState(null); // JWT token

  // When user logs in, join their private chat room
  useEffect(() => {
    if (user && user.id) {
      socket.emit('joinPrivateChat', user.id);
      console.log(`Joined private chat room: user-${user.id}`);
    }
  }, [user]);

  // If user is not logged in, show the Login component.
  if (!user) {
    return <Login setUser={setUser} setToken={setToken} />;
  }

  // Once logged in, show the Chat component.
  return (
    <div>
      <h1>Peer Learning Platform</h1>
      <Chat user={user} token={token} socket={socket} />
    </div>
  );
}

export default App;
