// src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { BrowserRouter, Routes, Route } from 'react-router-dom';
import Login from './Login';
import Chat from './Chat';
import QuestionsList from './QuestionsList';
import QuestionDetail from './QuestionDetail';

// Connect to your backend's Socket.io server.
const socket = io('http://localhost:5001');

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    if (user && user.id) {
      socket.emit('joinPrivateChat', user.id);
      console.log(`Joined private chat room: user-${user.id}`);
    }
  }, [user]);

  if (!user) {
    return <Login setUser={setUser} setToken={setToken} />;
  }

  return (
    <BrowserRouter>
      <div className="container">
        <h1>Peer Learning Platform</h1>
        {/* You can add a navigation bar here if desired */}
        <Routes>
          <Route path="/" element={<QuestionsList token={token} />} />
          <Route path="/question/:questionId" element={<QuestionDetail token={token} />} />
          <Route path="/chat" element={<Chat user={user} token={token} socket={socket} />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
