// src/GroupChat.js
import React, { useEffect, useState, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

function GroupChat({ user, token, socket }) {
  const { groupId } = useParams();
  const navigate = useNavigate();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  const listenerRegistered = useRef(false);

  useEffect(() => {
    console.log("GroupChat mounted, groupId =", groupId);
    socket.emit("joinGroup", groupId);
    console.log(`Joined group-${groupId}`);

    axios.get(`http://localhost:5001/api/group-chat/${groupId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => setMessages(res.data))
      .catch((err) => console.error("Error fetching group messages:", err));

    const handleGroupMessage = (messageData) => {
      console.log("New group message received:", messageData);
      if (parseInt(messageData.group_id) === parseInt(groupId)) {
        setMessages((prev) => [...prev, messageData]);
      }
    };

    // Register the listener (and clean up on unmount)
    socket.off("receiveGroupMessage", handleGroupMessage);
    if (!listenerRegistered.current) {
      socket.on("receiveGroupMessage", handleGroupMessage);
      listenerRegistered.current = true;
      console.log("Listener registered. Count:", socket.listeners("receiveGroupMessage").length);
    }

    return () => {
      console.log("Cleaning up listener for groupId =", groupId);
      socket.off("receiveGroupMessage", handleGroupMessage);
      listenerRegistered.current = false;
    };
  }, [groupId, token, socket]);

  const handleSendMessage = () => {
    if (!newMessage.trim()) return;
    const payload = {
      groupId: parseInt(groupId),
      userId: user.id,
      message: newMessage.trim()
    };
    socket.emit("sendGroupMessage", payload);
    setMessages((prev) => [
      ...prev,
      {
        group_id: groupId,
        user_id: user.id,
        message: newMessage.trim(),
        created_at: new Date().toISOString(),
        username: user.username
      }
    ]);
    setNewMessage('');
  };

  // Function to initiate a private chat when clicking on another user's name
  const initiatePrivateChat = (otherUserId) => {
    if (otherUserId !== user.id) {
      // Navigate to the private chat page and pass the recipient's ID as a query parameter
      navigate(`/chat?recipient=${otherUserId}`);
    }
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto' }}>
      <h2>Group Chat - Group {groupId}</h2>
      <div style={{ border: '1px solid #ccc', height: '300px', overflowY: 'scroll', marginBottom: '10px' }}>
        {messages.map((msg, idx) => (
          <div 
            key={idx} 
            style={{ 
              margin: '10px', 
              padding: '8px', 
              background: msg.user_id === user.id ? '#e0ffe0' : '#f0f0f0' 
            }}
          >
            <strong>
              {msg.user_id !== user.id ? (
                <span 
                  onClick={() => initiatePrivateChat(msg.user_id)}
                  style={{ cursor: 'pointer', color: 'blue' }}
                >
                  {msg.username}
                </span>
              ) : (
                msg.username
              )}
            </strong>: {msg.message}
            <div style={{ fontSize: '0.8em', color: '#555' }}>
              {new Date(msg.created_at).toLocaleTimeString()}
            </div>
          </div>
        ))}
      </div>
      <div>
        <input 
          type="text" 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
          placeholder="Type a message" 
          style={{ width: '80%', padding: '8px' }}
        />
        <button onClick={handleSendMessage} style={{ padding: '8px 16px', marginLeft: '10px' }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default GroupChat;
