import React, { useEffect, useState, useRef } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function GroupChat({ user, token, socket }) {
  const { groupId } = useParams();
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');
  // Use a ref to track if the listener is already registered.
  const isListenerRegistered = useRef(false);

  useEffect(() => {
    // Emit joinGroup only once per groupId change.
    socket.emit("joinGroup", groupId);
    console.log(`Joined group-${groupId}`);

    axios.get(`http://localhost:5001/api/group-chat/${groupId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => setMessages(res.data))
    .catch((err) => console.error("Error fetching group messages:", err));

    const handleGroupMessage = (messageData) => {
      console.log("New group message received:", messageData);
      if (parseInt(messageData.group_id) === parseInt(groupId)) {
        setMessages((prev) => [...prev, messageData]);
      }
    };

    // Register the listener only if it hasn’t been registered yet.
    if (!isListenerRegistered.current) {
      socket.on("receiveGroupMessage", handleGroupMessage);
      isListenerRegistered.current = true;
    }

    // Cleanup: Remove the listener when the component unmounts or when groupId changes.
    return () => {
      socket.off("receiveGroupMessage", handleGroupMessage);
      isListenerRegistered.current = false;
    };
  }, [groupId]); // Only run when groupId changes

  const handleSendMessage = () => {
    if (!newMessage.trim()) return;
    const payload = {
      groupId: parseInt(groupId),
      userId: user.id,
      message: newMessage.trim()
    };
    socket.emit("sendGroupMessage", payload);
    setMessages((prev) => [
      ...prev,
      { group_id: groupId, user_id: user.id, message: newMessage.trim(), created_at: new Date().toISOString(), username: user.username }
    ]);
    setNewMessage('');
  };

  return (
    <div style={{ maxWidth: '600px', margin: '0 auto' }}>
      <h2>Group Chat - Group {groupId}</h2>
      <div style={{ border: '1px solid #ccc', height: '300px', overflowY: 'scroll', marginBottom: '10px' }}>
        {messages.map((msg, idx) => (
          <div key={idx} style={{ margin: '10px', padding: '8px', background: msg.user_id === user.id ? '#e0ffe0' : '#f0f0f0' }}>
            <strong>{msg.username}</strong>: {msg.message}
            <div style={{ fontSize: '0.8em', color: '#555' }}>
              {new Date(msg.created_at).toLocaleTimeString()}
            </div>
          </div>
        ))}
      </div>
      <div>
        <input
          type="text"
          value={newMessage}
          onChange={(e) => setNewMessage(e.target.value)}
          style={{ width: '80%', padding: '8px' }}
          placeholder="Type a message"
        />
        <button onClick={handleSendMessage} style={{ padding: '8px 16px', marginLeft: '10px' }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default GroupChat;
