// src/QuestionDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function QuestionDetail({ token }) {
  const { questionId } = useParams(); // Get the question ID from the URL
  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [newAnswer, setNewAnswer] = useState('');
  const [error, setError] = useState('');
  const [editingAnswerId, setEditingAnswerId] = useState(null);
  const [editedBody, setEditedBody] = useState('');

  useEffect(() => {
    // Fetch the question details
    axios.get(`http://localhost:5001/api/questions/${questionId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => {
      setQuestion(res.data);
    })
    .catch((err) => {
      console.error("Error fetching question:", err);
      setError("Could not fetch question.");
    });

    // Fetch the answers for the question
    axios.get(`http://localhost:5001/api/answers/${questionId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => {
      setAnswers(res.data);
    })
    .catch((err) => {
      console.error("Error fetching answers:", err);
      setError("Could not fetch answers.");
    });
  }, [questionId, token]);

  // Handle new answer submission
  const handleSubmitAnswer = async (e) => {
    e.preventDefault();
    if (!newAnswer.trim()) return;

    try {
      const res = await axios.post(
        'http://localhost:5001/api/answers',
        { questionId, body: newAnswer },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const createdAnswer = res.data.answer;
      setAnswers([...answers, createdAnswer]);
      setNewAnswer('');
    } catch (err) {
      console.error("Error submitting answer:", err);
      setError("Failed to submit answer.");
    }
  };

  // Handle answer deletion
  const handleDeleteAnswer = async (answerId) => {
    try {
      await axios.delete(`http://localhost:5001/api/answers/${answerId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // Remove the answer from local state
      setAnswers(answers.filter(ans => ans.id !== answerId));
    } catch (err) {
      console.error("Error deleting answer:", err);
      setError("Failed to delete answer.");
    }
  };

  // Handle starting to edit an answer
  const handleStartEditing = (answerId, currentBody) => {
    setEditingAnswerId(answerId);
    setEditedBody(currentBody);
  };

  // Handle canceling the edit
  const handleCancelEditing = () => {
    setEditingAnswerId(null);
    setEditedBody('');
  };

  // Handle submitting the edited answer
  const handleSubmitEdit = async (answerId) => {
    try {
      const res = await axios.put(
        `http://localhost:5001/api/answers/${answerId}`,
        { body: editedBody },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // Update the local state with the edited answer
      setAnswers(answers.map(ans => (ans.id === answerId ? res.data.answer : ans)));
      setEditingAnswerId(null);
      setEditedBody('');
    } catch (err) {
      console.error("Error updating answer:", err);
      setError("Failed to update answer.");
    }
  };

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }
  if (!question) {
    return <div>Loading question...</div>;
  }

  return (
    <div style={{ maxWidth: '700px', margin: '20px auto' }}>
      <h2>{question.title}</h2>
      <p>{question.body}</p>
      {question.tags && <p><strong>Tags:</strong> {question.tags.join(', ')}</p>}

      <hr />
      <h3>Answers</h3>
      {answers.length === 0 ? (
        <p>No answers yet. Be the first to answer!</p>
      ) : (
        answers.map((ans) => (
          <div 
            key={ans.id} 
            style={{
              padding: '10px', 
              marginBottom: '10px', 
              border: '1px solid #ccc', 
              borderRadius: '5px', 
              background: '#f9f9f9'
            }}
          >
            {editingAnswerId === ans.id ? (
              <>
                <textarea
                  rows="3"
                  style={{ width: '100%', marginBottom: '10px', padding: '10px' }}
                  value={editedBody}
                  onChange={(e) => setEditedBody(e.target.value)}
                />
                <button onClick={() => handleSubmitEdit(ans.id)} style={{ marginRight: '10px' }}>Save</button>
                <button onClick={handleCancelEditing}>Cancel</button>
              </>
            ) : (
              <>
                <p>{ans.body}</p>
                <p style={{ fontSize: '0.8em', color: '#555' }}>
                  By {ans.username} on {new Date(ans.created_at).toLocaleString()}
                </p>
                <p style={{ fontWeight: 'bold' }}>Upvotes: {ans.upvotes}</p>
                {/* Only show edit/delete options if this answer belongs to the current user */}
                {ans.user_id === question.user_id && (
                  <div>
                    <button onClick={() => handleStartEditing(ans.id, ans.body)} style={{ marginRight: '10px' }}>
                      Edit
                    </button>
                    <button onClick={() => handleDeleteAnswer(ans.id)}>
                      Delete
                    </button>
                  </div>
                )}
                <div>
                  <button onClick={() => handleAnswerUpvote(ans.id)} style={{ marginRight: '10px' }}>
                    Upvote
                  </button>
                  <button onClick={() => handleAnswerDownvote(ans.id)}>
                    Downvote
                  </button>
                </div>
              </>
            )}
          </div>
        ))
      )}

      <hr />
      <h3>Submit Your Answer</h3>
      <form onSubmit={handleSubmitAnswer}>
        <textarea
          rows="4"
          style={{ width: '100%', marginBottom: '10px', padding: '10px' }}
          value={newAnswer}
          onChange={(e) => setNewAnswer(e.target.value)}
          placeholder="Type your answer..."
        />
        <button type="submit" style={{ padding: '8px 16px' }}>Submit Answer</button>
      </form>
    </div>
  );
}

export default QuestionDetail;
