// src/Chat.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Chat({ user, token, socket }) {
  const [recipientId, setRecipientId] = useState(''); // ID of the recipient to chat with (for sending messages)
  const [messages, setMessages] = useState([]); // Array to store all messages for the logged-in user
  const [newMessage, setNewMessage] = useState('');

  // Fetch existing private messages involving the logged-in user.
  useEffect(() => {
    // For simplicity, fetch messages using the recipientId if provided;
    // otherwise, fetch all messages involving the user.
    const fetchUrl = recipientId
      ? `http://localhost:5001/api/private-messages/${recipientId}`
      : `http://localhost:5001/api/private-messages/${user.id}`;

    axios.get(fetchUrl, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => {
        console.log("Fetched messages:", res.data);
        setMessages(res.data);
      })
      .catch((err) => console.error("Error fetching messages:", err));
  }, [recipientId, token, user.id]);

  // Listen for incoming private messages that involve the logged-in user.
// In src/Chat.js, inside the useEffect for socket events:
useEffect(() => {
  console.log("Setting up listener for receivePrivateMessage");
  socket.on("receivePrivateMessage", (messageData) => {
    console.log("DEBUG: Received private message:", messageData);
    // Append the message regardless of the sender/recipient
    setMessages((prevMessages) => [...prevMessages, messageData]);
  });
  return () => {
    socket.off("receivePrivateMessage");
  };
}, [socket]);


  // Handle sending a new message.
  const handleSendMessage = () => {
    if (!recipientId || !newMessage.trim()) return;

    const messagePayload = {
      senderId: user.id,
      recipientId,
      message: newMessage.trim()
    };

    console.log("Sending message:", messagePayload);
    socket.emit("sendPrivateMessage", messagePayload);

    // Optimistically update the UI with the new message.
    setMessages((prev) => [
      ...prev,
      {
        ...messagePayload,
        username: user.username,
        created_at: new Date().toISOString(),
        is_read: false
      }
    ]);

    setNewMessage('');
  };

  return (
    <div>
      <h2>Private Chat</h2>
      <div style={{ marginBottom: '15px' }}>
        <label>Recipient User ID:</label>
        <input 
          type="text" 
          value={recipientId} 
          onChange={(e) => setRecipientId(e.target.value)} 
          placeholder="Enter recipient's user id" 
          style={{ width: '200px', marginLeft: '10px' }} 
        />
      </div>
      <div 
        style={{ 
          border: '1px solid #ccc', 
          padding: '10px', 
          height: '300px', 
          overflowY: 'auto',
          marginBottom: '15px'
        }}
      >
        {messages.map((msg, idx) => (
          <div 
            key={idx} 
            style={{ 
              marginBottom: '10px', 
              padding: '8px', 
              borderRadius: '5px', 
              backgroundColor: msg.sender_id === user.id ? '#e0ffe0' : '#f0f0f0' 
            }}
          >
            <strong>
              {msg.username || (msg.sender_id === user.id ? user.username : 'User')}
            </strong>: {msg.message}
            <div style={{ fontSize: '0.8em', color: '#555' }}>
              {new Date(msg.created_at).toLocaleTimeString()}
            </div>
          </div>
        ))}
      </div>
      <div>
        <input 
          type="text" 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
          placeholder="Type your message" 
          style={{ width: '80%', padding: '10px' }}
        />
        <button onClick={handleSendMessage} style={{ marginLeft: '10px', padding: '10px 20px' }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default Chat;
