// src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import Login from './Login';
import Chat from './Chat';
import QuestionsList from './QuestionsList';

// Connect to your backend's Socket.io server.
const socket = io('http://localhost:5001');

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);

  useEffect(() => {
    if (user && user.id) {
      socket.emit('joinPrivateChat', user.id);
      console.log(`Joined private chat room: user-${user.id}`);
    }
  }, [user]);

  if (!user) {
    return <Login setUser={setUser} setToken={setToken} />;
  }

  return (
    <div className="container">
      <h1>Peer Learning Platform</h1>
      {/* Show chat and Q&A components */}
      <Chat user={user} token={token} socket={socket} />
      <QuestionsList token={token} />
    </div>
  );
}

export default App;
