// src/Chat.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ConversationSelector from './ConversationSelector';

function Chat({ user, token, socket }) {
  const [selectedConversation, setSelectedConversation] = useState(null);
  const [messages, setMessages] = useState([]);
  const [newMessage, setNewMessage] = useState('');

  // Fetch messages for the selected conversation
  useEffect(() => {
    if (!selectedConversation) return;
    axios.get(`http://localhost:5001/api/private-messages/${selectedConversation}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => {
      console.log("Fetched messages for conversation:", res.data);
      setMessages(res.data);
    })
    .catch((err) => console.error("Error fetching messages:", err));
  }, [selectedConversation, token]);

  // Listen for incoming messages that belong to the selected conversation
  useEffect(() => {
    socket.on("receivePrivateMessage", (messageData) => {
      console.log("DEBUG: Received private message:", messageData);
      // Check if this message is between the logged-in user and the selected conversation partner
      if (
        (messageData.sender_id.toString() === selectedConversation?.toString() &&
         messageData.recipient_id.toString() === user.id.toString()) ||
        (messageData.sender_id.toString() === user.id.toString() &&
         messageData.recipient_id.toString() === selectedConversation?.toString())
      ) {
        setMessages((prevMessages) => [...prevMessages, messageData]);
      }
    });
    return () => {
      socket.off("receivePrivateMessage");
    };
  }, [socket, selectedConversation, user.id]);

  const handleSendMessage = () => {
    if (!recipientId || !newMessage.trim()) return;

    const messagePayload = {
      senderId: user.id,
      recipientId,
      message: newMessage.trim()
    };

    console.log("Sending message:", messagePayload);
    socket.emit("sendPrivateMessage", messagePayload);



    setNewMessage('');
  };


  return (
    <div>
      <h2>Private Chat</h2>
      {/* Conversation Selector */}
      <ConversationSelector token={token} onSelect={setSelectedConversation} />

      {selectedConversation ? (
        <>
          <h3>Conversation with User {selectedConversation}</h3>
          <div 
            style={{ 
              border: '1px solid #ccc', 
              padding: '10px', 
              height: '300px', 
              overflowY: 'auto',
              marginBottom: '15px' 
            }}
          >
            {messages.map((msg, idx) => (
              <div 
                key={idx} 
                style={{ 
                  marginBottom: '10px', 
                  padding: '8px', 
                  borderRadius: '5px', 
                  backgroundColor: msg.sender_id === user.id ? '#e0ffe0' : '#f0f0f0',
                  color: '#333' // <-- Add this to ensure dark text
                }}
              >
                <strong>{msg.username || (msg.sender_id === user.id ? user.username : 'User')}</strong>: {msg.message}
  <div style={{ fontSize: '0.8em', color: '#555' }}>
    {new Date(msg.created_at).toLocaleTimeString()}
                </div>
              </div>
            ))}
          </div>
          <div>
            <input 
              type="text" 
              value={newMessage} 
              onChange={(e) => setNewMessage(e.target.value)} 
              placeholder="Type your message" 
              style={{ width: '80%', padding: '10px' }}
            />
            <button onClick={handleSendMessage} style={{ marginLeft: '10px', padding: '10px 20px' }}>
              Send
            </button>
          </div>
        </>
      ) : (
        <p>Please select a conversation to view messages.</p>
      )}
    </div>
  );
}

export default Chat;
