// src/QuestionDetail.js
import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

function QuestionDetail({ token }) {
  const { questionId } = useParams(); // Get the question ID from the URL
  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [newAnswer, setNewAnswer] = useState('');
  const [error, setError] = useState('');

  useEffect(() => {
    // Fetch the question details
    axios.get(`http://localhost:5001/api/questions/${questionId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => {
      // If your API returns an object, use it directly.
      // (If it returns an array, adjust accordingly.)
      setQuestion(res.data);
    })
    .catch((err) => {
      console.error("Error fetching question:", err);
      setError("Could not fetch question.");
    });

    // Fetch the answers for the question
    axios.get(`http://localhost:5001/api/answers/${questionId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => {
      setAnswers(res.data);
    })
    .catch((err) => {
      console.error("Error fetching answers:", err);
      setError("Could not fetch answers.");
    });
  }, [questionId, token]);

  // Handle new answer submission
  const handleSubmitAnswer = async (e) => {
    e.preventDefault();
    if (!newAnswer.trim()) return;

    try {
      // POST the new answer (ensure your backend supports this endpoint)
      const res = await axios.post(
        'http://localhost:5001/api/answers',
        { questionId, body: newAnswer },
        { headers: { Authorization: `Bearer ${token}` } }
      );

      // Assume the backend returns { message: "...", answer: { ... } }
      const createdAnswer = res.data.answer;
      // Append the new answer to the list
      setAnswers([...answers, createdAnswer]);
      setNewAnswer('');
    } catch (err) {
      console.error("Error submitting answer:", err);
      setError("Failed to submit answer.");
    }
  };

  if (error) {
    return <div style={{ color: 'red' }}>{error}</div>;
  }
  if (!question) {
    return <div>Loading question...</div>;
  }

  return (
    <div style={{ maxWidth: '700px', margin: '20px auto' }}>
      <h2>{question.title}</h2>
      <p>{question.body}</p>
      {question.tags && <p><strong>Tags:</strong> {question.tags.join(', ')}</p>}

      <hr />
      <h3>Answers</h3>
      {answers.length === 0 ? (
        <p>No answers yet. Be the first to answer!</p>
      ) : (
        answers.map((ans) => (
          <div 
            key={ans.id} 
            style={{
              padding: '10px', 
              marginBottom: '10px', 
              border: '1px solid #ccc', 
              borderRadius: '5px', 
              background: '#f9f9f9'
            }}
          >
            <p>{ans.body}</p>
            <p style={{ fontSize: '0.8em', color: '#555' }}>
              By {ans.username} on {new Date(ans.created_at).toLocaleString()}
            </p>
          </div>
        ))
      )}

      <hr />
      <h3>Submit Your Answer</h3>
      <form onSubmit={handleSubmitAnswer}>
        <textarea
          rows="4"
          style={{ width: '100%', marginBottom: '10px', padding: '10px' }}
          value={newAnswer}
          onChange={(e) => setNewAnswer(e.target.value)}
          placeholder="Type your answer..."
        />
        <button type="submit" style={{ padding: '8px 16px' }}>Submit Answer</button>
      </form>
    </div>
  );
}

export default QuestionDetail;
