// src/StudyGroupList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

function StudyGroupList({ token }) {
  const [groups, setGroups] = useState([]);

  useEffect(() => {
    // Fetch all study groups
    axios.get('http://localhost:5001/api/study-groups', {
      headers: { Authorization: `Bearer ${token}` }
    })
    .then((res) => setGroups(res.data))
    .catch((err) => console.error("Error fetching groups:", err));
  }, [token]);

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <h2>Study Groups</h2>
      {groups.length === 0 ? (
        <p>No study groups found.</p>
      ) : (
        groups.map((g) => (
          <div key={g.id} style={{ marginBottom: '10px' }}>
            <Link to={`/group-chat/${g.id}`} style={{ textDecoration: 'none', color: '#5151e5' }}>
              {g.name}
            </Link>
          </div>
        ))
      )}
    </div>
  );
}

export default StudyGroupList;
