// src/QuestionDetail.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';

function QuestionDetail({ token }) {
  const { questionId } = useParams();
  const [question, setQuestion] = useState(null);
  const [answers, setAnswers] = useState([]);
  const [newAnswer, setNewAnswer] = useState("");

  useEffect(() => {
    // Fetch question detail
    axios.get(`http://localhost:5001/api/questions/${questionId}`)
      .then((res) => {
        setQuestion(res.data);
      })
      .catch((err) => console.error("Error fetching question:", err));

    // Fetch answers for the question
    axios.get(`http://localhost:5001/api/answers/${questionId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => {
        setAnswers(res.data);
      })
      .catch((err) => console.error("Error fetching answers:", err));
  }, [questionId, token]);

  const handleSubmit = (e) => {
    e.preventDefault();
    if (newAnswer.trim() === "") return;
    // Post a new answer to the backend.
    axios.post(
      `http://localhost:5001/api/answers`,
      { questionId, body: newAnswer },
      { headers: { Authorization: `Bearer ${token}` } }
    )
      .then((res) => {
        // Assume the backend returns the new answer as res.data.answer.
        setAnswers((prev) => [...prev, res.data.answer]);
        setNewAnswer("");
      })
      .catch((err) => console.error("Error posting answer:", err));
  };

  if (!question) return <div>Loading question...</div>;

  return (
    <div>
      <h2>{question.title}</h2>
      <p>{question.body}</p>
      {question.tags && (
        <p>
          <strong>Tags:</strong> {question.tags.join(", ")}
        </p>
      )}
      <h3>Answers</h3>
      {answers.length === 0 ? (
        <p>No answers yet.</p>
      ) : (
        answers.map((ans, idx) => (
          <div key={idx} style={{ borderBottom: "1px solid #ccc", marginBottom: "10px", paddingBottom: "5px" }}>
            <p>{ans.body}</p>
            <small>Upvotes: {ans.upvotes} | Posted: {new Date(ans.created_at).toLocaleString()}</small>
          </div>
        ))
      )}
      <form onSubmit={handleSubmit}>
        <textarea
          value={newAnswer}
          onChange={(e) => setNewAnswer(e.target.value)}
          placeholder="Write your answer..."
          rows="4"
          style={{ width: "100%", marginBottom: "10px", padding: "10px" }}
        ></textarea>
        <button type="submit" style={{ padding: "10px 20px" }}>Post Answer</button>
      </form>
    </div>
  );
}

export default QuestionDetail;
