// src/Chat.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Chat({ user, token, socket }) {
  const [recipientId, setRecipientId] = useState(''); // ID of the recipient to chat with.
  const [messages, setMessages] = useState([]); // Array to store chat messages.
  const [newMessage, setNewMessage] = useState('');

  // Fetch existing private messages when the recipient changes.
  useEffect(() => {
    if (!recipientId) return;
    axios.get(`http://localhost:5001/api/private-messages/${recipientId}`, {
      headers: { Authorization: `Bearer ${token}` }
    })
      .then((res) => {
        console.log("Fetched messages:", res.data);
        setMessages(res.data);
      })
      .catch((err) => console.error("Error fetching messages:", err));
  }, [recipientId, token]);

  // Listen for incoming private messages.
  useEffect(() => {
    console.log("Setting up listener for receivePrivateMessage");
    socket.on("receivePrivateMessage", (messageData) => {
      console.log("Received private message:", messageData);
      // Only add the message if it is part of the current conversation.
      if (
        messageData.sender_id.toString() === recipientId ||
        messageData.recipient_id.toString() === recipientId
      ) {
        setMessages((prevMessages) => [...prevMessages, messageData]);
      }
    });

    // Clean up the event listener on unmount.
    return () => {
      socket.off("receivePrivateMessage");
    };
  }, [socket, recipientId]);

  // Handle sending a new message.
  const handleSendMessage = () => {
    if (!recipientId || !newMessage.trim()) return;

    const messagePayload = {
      senderId: user.id,
      recipientId,
      message: newMessage.trim()
    };

    console.log("Sending message:", messagePayload);
    socket.emit("sendPrivateMessage", messagePayload);

    // Optimistic update: show the message immediately.
    setMessages((prev) => [
      ...prev,
      {
        ...messagePayload,
        username: user.username,
        created_at: new Date().toISOString(),
        is_read: false
      }
    ]);

    setNewMessage('');
  };

  return (
    <div style={{ maxWidth: '600px', margin: '20px auto' }}>
      <h2>Private Chat</h2>
      <div style={{ marginBottom: '10px' }}>
        <label>Recipient User ID: </label>
        <input 
          type="text" 
          value={recipientId} 
          onChange={(e) => setRecipientId(e.target.value)} 
          placeholder="Enter recipient's user id" 
          style={{ width: '200px', marginLeft: '10px' }} 
        />
      </div>
      <div 
        style={{ 
          border: '1px solid #ccc', 
          padding: '10px', 
          height: '300px', 
          overflowY: 'auto',
          marginBottom: '10px'
        }}
      >
        {messages.map((msg, idx) => (
          <div 
            key={idx} 
            style={{ 
              marginBottom: '10px', 
              padding: '5px', 
              backgroundColor: msg.sender_id === user.id ? '#e0ffe0' : '#f0f0f0' 
            }}
          >
            <strong>
              {msg.username || (msg.sender_id === user.id ? user.username : 'User')}
            </strong>: {msg.message}
            <div style={{ fontSize: '0.8em', color: '#555' }}>
              {new Date(msg.created_at).toLocaleTimeString()}
            </div>
          </div>
        ))}
      </div>
      <div>
        <input 
          type="text" 
          value={newMessage} 
          onChange={(e) => setNewMessage(e.target.value)} 
          placeholder="Type your message" 
          style={{ width: '80%', padding: '8px' }}
        />
        <button onClick={handleSendMessage} style={{ padding: '8px 16px', marginLeft: '10px' }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default Chat;
