// src/App.js
import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { BrowserRouter, Routes, Route, Link } from 'react-router-dom';
import axios from 'axios';
import Login from './Login';
import Signup from './Signup'; // Import Signup component
import Chat from './Chat';
import QuestionsList from './QuestionsList';
import QuestionDetail from './QuestionDetail';
import AskQuestion from './AskQuestion';
import Notifications from './Notifications';
import StudyGroupList from './StudyGroupList';
import GroupChat from './GroupChat';
import UserProfile from './UserProfile';

const socket = io('http://localhost:5001');

function App() {
  const [user, setUser] = useState(null);
  const [token, setToken] = useState(null);

  // Check localStorage for token on mount
  useEffect(() => {
    const storedToken = localStorage.getItem("token");
    if (storedToken && !token) {
      setToken(storedToken);
      axios.get('http://localhost:5001/api/auth/profile', {
        headers: { Authorization: `Bearer ${storedToken}` }
      })
      .then((res) => {
        setUser(res.data.user);
        localStorage.setItem("user", JSON.stringify(res.data.user));
      })
      .catch((err) => {
        console.error("Error fetching user profile:", err);
        localStorage.removeItem("token");
        localStorage.removeItem("user");
      });
    }
  }, [token]);

  // Join private chat room once user is loaded
  useEffect(() => {
    if (user && user.id) {
      socket.emit('joinPrivateChat', user.id);
      console.log(`Joined private chat room: user-${user.id}`);
    }
  }, [user]);

  if (!user) {
    return (
      <BrowserRouter>
        <Routes>
          <Route path="/signup" element={<Signup setUser={setUser} setToken={setToken} />} />
          <Route path="/login" element={<Login setUser={setUser} setToken={setToken} />} />
          <Route path="*" element={<Login setUser={setUser} setToken={setToken} />} />
        </Routes>
      </BrowserRouter>
    );
  }

  return (
    <BrowserRouter>
      <div className="container">
        <header style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          <h1>Peer Learning Platform</h1>
          <nav style={{ marginBottom: '20px' }}>
            <Link to="/chat" style={{ marginRight: '20px' }}>Private Chat</Link>
            <Link to="/group-list" style={{ marginRight: '20px' }}>Study Groups</Link>
            <Link to="/ask" style={{ textDecoration: 'none', color: '#5151e5', fontWeight: 'bold' }}>
              Ask a Question
            </Link>
            <Link to="/profile" style={{ marginLeft: '20px' }}>Profile</Link>
          </nav>
          <Notifications user={user} socket={socket} />
        </header>
        <Routes>
          {/* Home route shows the Questions List */}
          <Route path="/" element={<QuestionsList token={token} />} />
          {/* Question detail route */}
          <Route path="/question/:questionId" element={<QuestionDetail token={token} user={user} />} />
          {/* Ask a question */}
          <Route path="/ask" element={<AskQuestion token={token} />} />
          {/* Private chat page */}
          <Route path="/chat" element={<Chat user={user} token={token} socket={socket} />} />
          {/* Study group list */}
          <Route path="/group-list" element={<StudyGroupList token={token} />} />
          {/* Group chat page */}
          <Route path="/group-chat/:groupId" element={<GroupChat user={user} token={token} socket={socket} />} />
          {/* User Profile */}
          <Route path="/profile" element={<UserProfile token={token} user={user} />} />
        </Routes>
      </div>
    </BrowserRouter>
  );
}

export default App;
